import React, { useState } from 'react';
import './Hero.css';

const FunctionArea = () => {
  const [inputText, setInputText] = useState('');
  const [outputText, setOutputText] = useState('');

  const handleInputChange = (e) => {
    setInputText(e.target.value);
  };

  const handleGenerate = () => {
    // 未来这里将集成OpenAI API调用功能
    // 目前只是一个简单的演示
    setOutputText(`这里将显示基于输入内容"${inputText}"生成的回复内容。未来将通过OpenAI API生成实际的客服回复。`);
  };

  return (
    <div className="function-area">
      <h2 className="function-title">智能客服对话生成</h2>
      <p className="function-subtitle">输入客户对话，获取AI生成的专业回复</p>
      
      <div className="input-area">
        <textarea 
          placeholder="请输入客户的对话内容..." 
          value={inputText}
          onChange={handleInputChange}
        />
      </div>
      
      <div className="output-area">
        <div className="output-content">
          {outputText || '生成的回复将显示在这里...'}
        </div>
      </div>
      
      <button className="generate-button" onClick={handleGenerate}>
        生成回复
      </button>
    </div>
  );
};

export default FunctionArea;
