import React, { useState } from 'react';
import Header from '../../components/Header/Header';
import Footer from '../../components/Footer/Footer';
import './ChatPage.css';

const ChatPage = () => {
  const [inputText, setInputText] = useState('');
  const [responseOptions, setResponseOptions] = useState([]);
  const [selectedResponse, setSelectedResponse] = useState('');
  const [customerThoughts, setCustomerThoughts] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [isTaskbarOpen, setIsTaskbarOpen] = useState(true);
  const [chatHistory] = useState([
    { id: 1, title: '今天你要谈成这笔单吗?', date: '今天' },
    { id: 2, title: '微信客服自动化实现', date: '前7天' },
    { id: 3, title: '产品价格咨询对话', date: '前7天' },
    { id: 4, title: '售后服务问题处理', date: '前30天' },
    { id: 5, title: '客户投诉解决方案', date: '前30天' },
  ]);
  const [tasks] = useState([
    { id: 1, title: '打动客户A，循环"你们真比别家"的卖点' },
    { id: 2, title: '1. 试讲八项卖点赢客户' },
    { id: 3, title: '2. 理顺客户价格' },
    { id: 4, title: '3. 提出定价策略' },
  ]);
  const [activeChat, setActiveChat] = useState(1);

  const API_KEY = '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';
  const API_URL = 'https://openrouter.ai/api/v1/chat/completions';

  const handleInputChange = (e) => {
    setInputText(e.target.value);
  };

  const handleGenerate = async () => {
    if (!inputText.trim()) return;

    setIsLoading(true);
    setResponseOptions([]);
    setSelectedResponse('');
    setCustomerThoughts('');
    setError('');

    try {
      console.log('正在调用API...');

      const requestBody = {
        model: 'openai/gpt-4o-search-preview',
        messages: [
          {
            role: 'system',
            content: `你是一个销售助手AI，帮助销售人员分析客户需求和想法，提供有效的回复话术。
            请分析用户输入的客户对话，提供以下内容：
            1. 三条不同的可能回复话术，每条话术针对不同角度或策略
            2. 对客户潜在想法的分析，包括可能的考虑点、顾虑和兴趣点

            请使用以下JSON格式返回：
            {
              "responses": ["回复1", "回复2", "回复3"],
              "customerThoughts": "客户想法分析内容"
            }`
          },
          {
            role: 'user',
            content: inputText
          }
        ],
        temperature: 0.7,
        max_tokens: 1000
      };

      console.log('请求内容:', requestBody);

      // 使用try-catch包装fetch调用以捕获网络相关错误
      try {
        const response = await fetch(API_URL, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${API_KEY}`,
            'HTTP-Referer': window.location.href,
            'X-Title': 'TalkPro'
          },
          body: JSON.stringify(requestBody)
        });

        console.log('API状态码:', response.status);

        // 处理错误情况
        if (!response.ok) {
          const errorText = await response.text();
          console.error('API错误响应:', errorText);
          throw new Error(`API请求失败: ${response.status} ${response.statusText}. 详情: ${errorText}`);
        }

        const data = await response.json();
        console.log('API响应数据:', data);

        if (data.choices && data.choices[0] && data.choices[0].message) {
          try {
            // 尝试解析返回的JSON
            const content = data.choices[0].message.content;
            const parsedContent = JSON.parse(content);

            if (parsedContent.responses && Array.isArray(parsedContent.responses) && parsedContent.responses.length > 0) {
              setResponseOptions(parsedContent.responses);
              setSelectedResponse(parsedContent.responses[0]);
            } else {
              setResponseOptions([]);
              setSelectedResponse("API返回格式错误，未能获取回复选项");
            }

            if (parsedContent.customerThoughts) {
              setCustomerThoughts(parsedContent.customerThoughts);
            } else {
              setCustomerThoughts("未能获取客户想法分析");
            }
          } catch (parseError) {
            console.error('解析API响应失败:', parseError);
            // 如果解析失败，尝试直接使用返回的内容
            const content = data.choices[0].message.content;
            setSelectedResponse(content);
            setCustomerThoughts("API返回格式不正确，无法解析客户想法");
          }
        } else {
          setSelectedResponse("API调用失败，未返回有效数据");
          setCustomerThoughts("API调用失败，未能分析客户想法");
        }
      } catch (fetchError) {
        console.error('网络请求错误:', fetchError);
        throw new Error(`网络请求错误: ${fetchError.message}`);
      }
    } catch (error) {
      console.error('API调用失败:', error);
      setError(`调用API时出错: ${error.message}`);
      setSelectedResponse("");
      setCustomerThoughts("");
    } finally {
      setIsLoading(false);
    }
  };

  const handleCopy = () => {
    if (!selectedResponse) return;

    navigator.clipboard.writeText(selectedResponse)
      .then(() => {
        alert('已复制到剪贴板');
      })
      .catch(err => {
        console.error('复制失败:', err);
      });
  };

  const selectResponse = (response) => {
    setSelectedResponse(response);
  };

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const toggleTaskbar = () => {
    setIsTaskbarOpen(!isTaskbarOpen);
  };

  const selectChat = (id) => {
    setActiveChat(id);
    // 在实际应用中，这里会加载对应的聊天记录
  };

  return (
    <div className="chat-page">
      <Header />
      <main className="chat-content">
        {/* 左侧聊天记录边栏 */}
        <div className={`chat-sidebar ${isSidebarOpen ? 'open' : 'closed'}`}>
          <div className="sidebar-header">
            <h3>聊天记录</h3>
            <button className="new-chat-btn">
              <span className="plus-icon">+</span> 新聊天
            </button>
          </div>

          <div className="chat-list">
            {chatHistory.map(chat => (
              <div 
                key={chat.id} 
                className={`chat-item ${activeChat === chat.id ? 'active' : ''}`}
                onClick={() => selectChat(chat.id)}
              >
                <div className="chat-item-title">{chat.title}</div>
                <div className="chat-item-date">{chat.date}</div>
              </div>
            ))}
          </div>
        </div>

        {/* 中间主要内容区域 */}
        <div className="chat-main-container">
          <button className="toggle-sidebar-btn" onClick={toggleSidebar}>
            {isSidebarOpen ? '关闭边栏' : '打开边栏'}
          </button>

          {/* 当任务栏关闭时显示的打开按钮 */}
          {!isTaskbarOpen && (
            <button className="toggle-taskbar-open-btn" onClick={toggleTaskbar}>
              打开任务栏
            </button>
          )}

          <div className="container">
            <div className="chat-header">
              <h1>今天你要谈成这笔单吗?</h1>
              <p>TalkPro会陪你一起分析客户，拆解异议，优化话术，直到拿下单子</p>
            </div>

            {/* 错误提示 */}
            {error && (
              <div className="error-message">
                <p>{error}</p>
                <button onClick={() => setError('')}>关闭</button>
              </div>
            )}

            <div className="chat-interface">
              <div className="input-output-container">
                {/* 左侧用户输入区域 */}
                <div className="user-input-section">
                  <h2>用户输入</h2>
                  <p className="section-subtitle">客户背景 & 场景描述</p>
                  <textarea 
                    placeholder="请输入客户背景和场景描述..." 
                    value={inputText}
                    onChange={handleInputChange}
                  />
                  <button 
                    className="generate-button"
                    onClick={handleGenerate}
                    disabled={!inputText.trim() || isLoading}
                  >
                    {isLoading ? '生成中...' : '生成回复'}
                  </button>
                </div>

                {/* 右侧AI输出区域 */}
                <div className="ai-output-section">
                  <div className="section-header">
                    <h2>AI生成输出</h2>
                    {selectedResponse && (
                      <button className="copy-button" onClick={handleCopy}>
                        复制
                      </button>
                    )}
                  </div>

                  {/* 回复选项 */}
                  {responseOptions.length > 0 && !isLoading && (
                    <div className="response-options">
                      {responseOptions.map((response, index) => (
                        <div 
                          key={index}
                          className={`response-option ${selectedResponse === response ? 'selected' : ''}`}
                          onClick={() => selectResponse(response)}
                        >
                          <div className="response-number">{index + 1}</div>
                          <div className="response-preview">{response.substring(0, 40)}...</div>
                        </div>
                      ))}
                    </div>
                  )}

                  {/* 选中的回复内容 */}
                  <div className="output-content">
                    {isLoading ? (
                      <div className="loading">生成中...</div>
                    ) : (
                      selectedResponse || <div className="placeholder">AI生成的回复将显示在这里</div>
                    )}
                  </div>

                  {/* 客户想法分析 */}
                  <div className="customer-thoughts">
                    <h3>客户想法分析</h3>
                    <div className="thoughts-content">
                      {isLoading ? (
                        <div className="loading">分析中...</div>
                      ) : (
                        customerThoughts || <div className="placeholder">AI分析的客户潜在想法将显示在这里</div>
                      )}
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* 右侧任务导向边栏 */}
        <div className={`task-sidebar ${isTaskbarOpen ? 'open' : 'closed'}`}>
          <div className="task-sidebar-header">
            <h3>任务导向</h3>
            <button className="toggle-taskbar-btn" onClick={toggleTaskbar}>
              {isTaskbarOpen ? '收起' : '展开'}
            </button>
          </div>

          <div className="task-list">
            {tasks.map(task => (
              <div key={task.id} className="task-item">
                <div className="task-item-title">{task.title}</div>
              </div>
            ))}
          </div>
        </div>
      </main>
      <Footer />
    </div>
  );
};

export default ChatPage;
