import React, { useState } from 'react';
import Header from '../../components/Header/Header';
import Footer from '../../components/Footer/Footer';
import './ChatPage.css';

const ChatPage = () => {
  const [inputText, setInputText] = useState('');
  const [outputText, setOutputText] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleInputChange = (e) => {
    setInputText(e.target.value);
  };

  const handleGenerate = () => {
    // 模拟API调用
    setIsLoading(true);
    setTimeout(() => {
      setOutputText(`这是基于输入"${inputText}"生成的AI回复内容。\n\n这里展示了一个模拟的回复，实际使用时将调用OpenAI API生成专业的客服回复。`);
      setIsLoading(false);
    }, 1500);
  };

  const handleCopy = () => {
    navigator.clipboard.writeText(outputText)
      .then(() => {
        alert('已复制到剪贴板');
      })
      .catch(err => {
        console.error('复制失败:', err);
      });
  };

  return (
    <div className="chat-page">
      <Header />
      <main className="chat-content">
        <div className="container">
          <div className="chat-header">
            <h1>智能对话助手</h1>
            <p>输入客户对话，获取AI生成的专业回复</p>
          </div>
          
          <div className="chat-interface">
            <div className="input-section">
              <h2>客户对话</h2>
              <textarea 
                placeholder="请输入客户的对话内容..." 
                value={inputText}
                onChange={handleInputChange}
              />
            </div>
            
            <div className="output-section">
              <div className="output-header">
                <h2>AI回复</h2>
                {outputText && (
                  <button className="copy-button" onClick={handleCopy}>
                    复制
                  </button>
                )}
              </div>
              <div className="output-content">
                {isLoading ? (
                  <div className="loading">生成中...</div>
                ) : (
                  outputText || <div className="placeholder">AI生成的回复将显示在这里</div>
                )}
              </div>
            </div>
            
            <button 
              className="generate-button"
              onClick={handleGenerate}
              disabled={!inputText.trim() || isLoading}
            >
              {isLoading ? '生成中...' : '生成回复'}
            </button>
          </div>
        </div>
      </main>
      <Footer />
    </div>
  );
};

export default ChatPage;
